$(document).ready((function(){"use strict";document.querySelectorAll(".password-showHide").forEach((item=>{let inputField=item.querySelector('input[type="password"], input[type="text"]'),iconBox=item.querySelector(".password-toggle-icon");iconBox.addEventListener("click",(()=>{"password"===inputField.type?(inputField.type="text",iconBox.innerHTML='<i class="fas fa-eye"></i>'):(inputField.type="password",iconBox.innerHTML='<i class="fas fa-eye-slash"></i>')}))}))}));